import { BaseNodeUnion } from "../base.js";
export const Statement = class extends BaseNodeUnion {
  // type = 'Statement' as const;
  constructor(ctx, visitor) {
    super(
      ctx,
      [
        ctx.block(),
        ctx.simpleStatement(),
        ctx.ifStatement(),
        ctx.forStatement(),
        ctx.whileStatement(),
        ctx.doWhileStatement(),
        ctx.continueStatement(),
        ctx.breakStatement(),
        ctx.tryStatement(),
        ctx.returnStatement(),
        ctx.emitStatement(),
        ctx.revertStatement(),
        ctx.assemblyStatement()
      ],
      visitor
    );
  }
};
